  return (
    <>
      <Navbar />
      <table className="table mt-5 w-75 p-5 m-auto text-center table-bordered">
        <thead>
          <tr>
            <td>S.No.</td>
            <td>Product Image</td>
            <td> Product info</td>
            <td>Action</td>
          </tr>
        </thead>

